export async function getShopItems() {
    return await fetch(`/api/homepage/`, {
      method: "GET",
      headers: { "Content-Type": "application/json" },
    })
      .then(async (response) => {
        // If request is not successful, display error message
        if (!response.ok) {
          throw new Error("MY CODE  =  HTTP status " + response.status);
        }
        return await response.json();
      }).catch((err) => {
        console.log(err);
      });
  }
