module.exports.getShopItems = async function() {
    return await fetch("/api/homepage", {
        method: "GET",
        headers: { "Content-Type": "application/json" }
    }).then((response) => {
        if(!response) {
            throw new Error("HTTP status " + response);
        }
        return response.json();
    }).catch(err => console.log(err));
}
