module.exports.getShopItems = async function getShopItems() {
    return await fetch("/api/homepage", {
        method: "GET",
        headers: {"Content-Type": "applicatio/json"}
    }).then(response => {
        if(!response) {
            throw new Error("HTTP status " + response);
        }
        return response.json();
    }).catch(err => {
        console.log(err);
    });
}
