export async function getShopItems() {
    await fetch(`/api/homepage/`, {
      method: "GET",
      headers: { "Content-Type": "application/json" },
    }).then(resp => {
      // you'll need to supply the function that checks the status here
      if (resp.ok) {
        return resp.json();
      } else {
        throw new Error(`Got back ${resp.status}`);
      }
   }).then(data => {
      console.log(data);
      items = data;
   }).catch((err) => {
