import React, {useState, useEffect} from 'react';
import { getShopItems } from "../scripts/api-scripts";

const getItems = async() => {
    let items = await getShopItems();
    console.log("in getItems() > "+items);
    return items;
}

function Shop(props){
    const [items, setItems] = useState([{name: ''}]);

    const change = () => {
        let temp = items;
        setTimeout(() => { 
            temp = getItems();
            console.log('in setTimeout >' + temp);
        }, 500);

        setItems()
    };

    return (<div className="ShopContent">
        <p onClick={change}>aaaaa {items[0].name}</p>
    </div>);
};

export default Shop;
